import { React, useEffect, useState } from "react";
import "./TodoNew.css";
import axios from "axios";
import { Table, Tag, Space, Button, Input, Row, Col, Pagination } from "antd";
import { EditTwoTone, DeleteTwoTone } from "@ant-design/icons";
import EditModal from "./EditModal";
import AddModal from "./AddModal";

function Paging() {
  const [contacts, setContacts] = useState([]);
  const [pagination, setPagination] = useState({ current: 1, pageSize: 5 });
  const [paramsContact, setParamsContact] = useState({ limit: 5, offset: 0 });
  const [loading, setLoading] = useState(false);

  const fetch = (page) => {
    setLoading(true);
    axios
      .get(
        "https://gateway.smartcontactcenter.xyz/smart-contact-center-contact-list-management/api/v1/omni-contact-list-normalizations",
        {
          params: {
            filter: {
              limit: page.limit,
              offset: page.offset,
            },
          },
        }
      )
      .then((res) => {
        let params = res.data.pop();
        setContacts(res.data);
        setPagination({
          total: params.total,
        });
        setParamsContact({
          limit: params.limit,
          offset: params.offset,
        });
        setLoading(false);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    fetch(paramsContact);
  }, []);

  const handleTableChange = (page, pageSize) => {
    let currentPage = {
      current: (page.current - 1) * page.pageSize,
      pageSize: pageSize,
    };
    console.log(page);
    fetch(currentPage);
  };

