import { React, useEffect, useState } from "react";
import "./TodoNew.css";
import axios from "axios";
import { Table, Tag, Space, Button, Input, Row, Col, Pagination } from "antd";
import { EditTwoTone, DeleteTwoTone } from "@ant-design/icons";
import EditModal from "./EditModal";
import AddModal from "./AddModal";

function Paging() {
  const [contacts, setContacts] = useState([]);
  const [pagination, setPagination] = useState({ current: 1, pageSize: 5 });
  const [paramsContact, setParamsContact] = useState({limit: 5, offset: 0});
  const [loading, setLoading] = useState(false);
  const [editVisible, setEditVisible] = useState(false);
  const [addVisible, setAddVisible] = useState(false);
  const [contact, setContact] = useState([]);

  const fetch = (page) => {
    setLoading(true);
    axios
      .get(
        "https://gateway.smartcontactcenter.xyz/smart-contact-center-contact-list-management/api/v1/omni-contact-list-normalizations",
        {
          params: {
            filter: {
              limit: page.limit,
              offset: page.offset,
            },
          },
        }
      )
      .then((res) => {
        let params = res.data.pop();
        setContacts(res.data);
        // setPagination({
        //   current: params.offset,
        //   pageSize: params.limit,
        //   total: params.total,
        // });
        setParamsContact({
          offset: params.offset,
        })
        setLoading(false);
      })
      .catch((err) => {
        console.log(err);
      });
  }

  useEffect(() => {
    fetch(pagination);
  }, []);

  const handleTableChange = (page, pageSize) => {
    let currentPage = {current: (page.current - 1) * page.pageSize, pageSize: pageSize}
    fetch(currentPage);
  }


  //define table
  const columns = [
    {
      title: "id",
      dataIndex: "id",
      key: "id",
      width: "15%",
    },
    {
      title: "Họ tên",
      dataIndex: "ho_va_ten",
      key: "ho_va_ten",
    },
    {
      title: "Số điện thoại",
      dataIndex: "sdt",
      key: "sdt",
    },
    {
      title: "Giới tính",
      dataIndex: "gender",
      key: "gender",
      render: (gender) => {
        let color;
        if (gender === "male") {
          color = "volcano";
        }
        if (gender === "female") {
          color = "green";
        }
        return (
          <Tag color={color} key={gender}>
            {gender.toString().toUpperCase()}
          </Tag>
        );
      },
    },
    {
      title: "",
      key: "action",
      render: (text, record) => (
        <Space size="middle">
          <EditTwoTone
            className="edit-button"
            
          />
          <DeleteTwoTone
            className="delete"
         
          />
        </Space>
      ),
    },
  ];

  //define data for table
  const data = contacts;
  // const tablePagination = {
  //   pageSize: pagination.pageSize,
  //   page: pagination.current / pagination.pageSize + 1,
  //   total: pagination.total,
  //   pageSizeOptions: ["10", "20", "30", "50", "100"],
  //   showSizeChanger: true,
  //   size: "default",
  // };

  return (
    <div className="TodoNew">
      <Row>
        <Col span={6}>
          <Search
            className="searchTxb"
            placeholder="input search text"
            allowClear
            enterButton="Search"
            size="large"
            onSearch={onSearch}
          />
        </Col>
        <Col span={6}></Col>
        <Col span={6}></Col>
        <Col span={6} className="addButton">
          <Button type="primary" size="large">
            +Add
          </Button>
        </Col>
      </Row>

      <Table
        columns={columns}
        dataSource={data}
        pagination={pagination}
        loading={loading}
        onChange={handleTableChange}
      />

      <EditModal
        
      />
      <AddModal
       
      />
    </div>
  );
}

export default Paging;
