import { React, Component } from "react";
import TodoItem from "./TodoItem";
import axios from "axios";

class TodoGeneral extends Component {
  constructor(props) {
    super(props);
    this.state = { todoItems: [] };
  }

  componentDidMount() {
    axios
      .get("https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data")
      .then((res) => {
        const todoItems = res.data;
        this.setState({ todoItems });
      })
      .catch((err) => {
        console.error(err);
      });
  }

  componentDidUpdate(props, state) {
    setTimeout(() => {
      console.log("this props: " + JSON.stringify(props));
      const { path } = props;
      if (path === "active") {
        axios
          .get("https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data")
          .then((res) => {
            let todoItems = res.data;
            let n = todoItems.length;
            for (let i = 0; i < n; i++) {
              if (todoItems[i].isCompleted === true) {
                todoItems.splice(i, 1);
                i--;
                n--;
              }
            }
            this.setState({ todoItems: todoItems });
          })
          .catch((err) => {
            console.error(err);
          });
      } else if (path === "completed") {
        axios
          .get("https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data")
          .then((res) => {
            let todoItems = res.data;
            let n = todoItems.length;
            for (let i = 0; i < n; i++) {
              if (todoItems[i].isCompleted === false) {
                todoItems.splice(i, 1);
                i--;
                n--;
              }
            }

            this.setState({ todoItems: todoItems });
          })
          .catch((err) => {
            console.error(err);
          });
      } else {
        axios
          .get("https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data")
          .then((res) => {
            const todoItems = res.data;
            this.setState({ todoItems: todoItems });
          })
          .catch((err) => {
            console.error(err);
          });
      }
    }, 5000)
  }


  
  render() {
    const { todoItems, onClick, onDoubleClick, onChange, onBlur, on} = this.state;

    return (
      <div>
        {todoItems.map((item, index) => (
          <TodoItem
            key={index}
            item={item}
            onClick={this.onItemClicked(item)}
            onDoubleClick={this.onDoubleClick(item)}
            onChange={(event) => this.onChange(event, item)}
            onBlur={this.onBlur(item)}
            onDelete={this.onDelete(item)}
          />
        ))}
      </div>
    );
  }
}

export default TodoGeneral;
