import React, { Component } from "react";
import "./App.css";
import TodoItem from "./components/TodoItem";
import TodoInput from "./components/TodoInput";
import TodoFilter from "./components/TodoFilter";
import "antd/dist/antd.css";
import axios from "axios";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import All from "./components/All";
import Active from "./components/Active";
import Completed from "./components/Completed";

class App extends Component {
  constructor() {
    super();
    //create state
    this.state = { todoItems: [] };

    //create function non parameter
    this.onPressEnter = this.onPressEnter.bind(this);
    this.chooseAll = this.chooseAll.bind(this);
    this.clearCompleted = this.clearCompleted.bind(this);
  }

  componentDidMount() {
    axios
      .get("https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data")
      .then((res) => {
        const todoItems = res.data;
        this.setState({ todoItems });
      })
      .catch((err) => {
        console.error(err);
      });
  }

  //to mark complete function
  findIndex(todoItems, item) {
    for (let i = 0; i < todoItems.length; i++) {
      if (todoItems[i].id === item.id) {
        return i;
      }
    }
  }

  onItemClicked(item) {
    return () => {
      const isCompleted = item.isCompleted;
      const { todoItems } = this.state;
      const index = this.findIndex(todoItems, item);

      axios
        .put("https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data", {
          isCompleted: !isCompleted
        })
        .then((response) => {
          this.setState({
            todoItems: [
              ...todoItems.slice(0, index),
              {
                ...item,
                isCompleted: !isCompleted,
              },
              ...todoItems.slice(index + 1),
            ],
          });
        })
        .catch((error) => {
          console.log(error);
        });
    };
  }

  //double click to edit text
  onDoubleClick(item) {
    return () => {
      const { todoItems } = this.state;
      const index = todoItems.indexOf(item);
      this.setState({
        todoItems: [
          ...todoItems.slice(0, index),
          {
            ...item,
            isEditing: true,
            isCompleted: item.isCompleted,
          },
          ...todoItems.slice(index + 1),
        ],
      });
    };
  }

  //save text afer edit (click outside textbox)
  onChange(event, item) {
    const text = event.target.value;
    const title = item.title;
    const isCompleted = item.isCompleted;
    const { todoItems } = this.state;
    const index = this.findIndex(todoItems, item);
    const newState = {
      todoItems: [
        ...todoItems.slice(0, index),
        {
          ...item,
          title: text,
          isEditing: true,
          isCompleted: isCompleted,
        },
        ...todoItems.slice(index + 1),
      ],
    };
    this.setState(newState);
    return title;
  }

  onBlur(item) {
    return () => {
      const text = item.title;
      const isCompleted = item.isCompleted;
      const { todoItems } = JSON.parse(window.localStorage.getItem("listItem"));
      const index = this.findIndex(todoItems, item);
      console.log(todoItems);
      console.log(item);
      const newState = {
        todoItems: [
          ...todoItems.slice(0, index),
          {
            ...item,
            title: text,
            isEditing: false,
            isCompleted: isCompleted,
          },
          ...todoItems.slice(index + 1),
        ],
      };
      this.setState(newState);
    };
  }

  //delete todo
  onDelete(item) {
    return () => {
      const { todoItems } = JSON.parse(window.localStorage.getItem("listItem"));
      const index = this.findIndex(todoItems, item);
      const newState = {
        todoItems: [
          ...todoItems.slice(0, index),
          ...todoItems.slice(index + 1),
        ],
      };
      this.setState(newState);
    };
  }

  //add todo
  onPressEnter(event) {
    if (event.key === "Enter") {
      const { todoItems } = JSON.parse(window.localStorage.getItem("listItem"));
      const n = todoItems.length + 1;
      todoItems.push({
        id: n,
        title: event.target.value,
        isEditing: false,
        isCompleted: false,
      });
      this.setState({ todoItems: todoItems });
      event.target.value = "";
    }
  }

  //click image to complete all
  chooseAll() {
    const { todoItems } = this.state;
    let count = 0;
    for (let item of todoItems) {
      if (item.isCompleted === true) {
        count++;
      }
    }
    if (count === todoItems.length) {
      for (let item of todoItems) {
        item.isCompleted = false;
      }
    } else {
      for (let item of todoItems) {
        item.isCompleted = true;
      }
    }

    const newState = { todoItems: todoItems };

    this.setState(newState);
  }

  //show all todo

  clearCompleted() {
    const { todoItems } = JSON.parse(window.localStorage.getItem("listItem"));
    let n = todoItems.length;
    for (let i = 0; i < n; i++) {
      if (todoItems[i].isCompleted === true) {
        todoItems.splice(i, 1);
        i--;
        n--;
      }
    }

    const newState = { todoItems: todoItems };
    this.setState(newState);
  }

  countActive() {
    const { todoItems } = this.state;
    let n = todoItems.length;
    let count = 0;
    for (let i = 0; i < n; i++) {
      if (todoItems[i].isCompleted === true) {
        count++;
      }
    }
    return n - count;
  }

  render() {
    const { todoItems } = this.state;
    const numItem = this.countActive();
    return (
      <div className="App">
        <p className="title">todos</p>
        <TodoInput onKeyUp={this.onPressEnter} chooseAll={this.chooseAll} />
        {todoItems.map((item, index) => (
          <TodoItem
            key={index}
            item={item}
            onClick={this.onItemClicked(item)}
            onDoubleClick={this.onDoubleClick(item)}
            onChange={(event) => this.onChange(event, item)}
            onBlur={this.onBlur(item)}
            onDelete={this.onDelete(item)}
          />
        ))}
        {/* <Router>
          <TodoFilter
            numItem={numItem}
            todoItems={todoItems}
            clearCompleted={this.clearCompleted}
          />
          <Switch>
            <Route exact path="/all">
              <All />
            </Route>
            <Route exact path="/active">
              <Active />
            </Route>
            <Route exact path="/completed">
              <Completed />
            </Route>
          </Switch>
        </Router> */}
      </div>
    );
  }
}

export default App;
