import { React, useEffect, useState } from "react";
import "./TodoNew.css";
import axios from "axios";
import { Table, Tag, Space, Button, Input } from "antd";
import { EditTwoTone, DeleteTwoTone } from "@ant-design/icons";
import EditModal from "./EditModal";
import AddModal from "./AddModal";
const { Search } = Input;
const onSearch = (value) => console.log(value);

function TodoNew() {
  const [todoItems, setTodo] = useState([]);

  useEffect(() => {
    axios
      .get("https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data")
      .then((res) => {
        setTodo(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  //click edit
  const onClickEdit = (item) => {
    console.log(item);
  };

  //click delete
  const onClickDelete = (item) => {
    axios
      .delete(
        "https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data/" +
          item.id
      )
      .then((response) => {
        console.log(response);
      });
  };

  //define table
  const columns = [
    {
      title: "id",
      dataIndex: "id",
      key: "id",
    },
    {
      title: "Title",
      dataIndex: "title",
      key: "title",
    },
    {
      title: "Is editing?",
      dataIndex: "isEditing",
      key: "isEditing",
      render: (isEditing) => {
        let color;
        if (isEditing === false) {
          color = "volcano";
        }
        if (isEditing === true) {
          color = "green";
        }
        return (
          <Tag color={color} key={isEditing}>
            {isEditing.toString().toUpperCase()}
          </Tag>
        );
      },
    },
    {
      title: "Is complete?",
      dataIndex: "isCompleted",
      key: "isCompleted",
      render: (isCompleted) => {
        let color;
        if (isCompleted === false) {
          color = "volcano";
        }
        if (isCompleted === true) {
          color = "green";
        }
        return (
          <Tag color={color} key={isCompleted}>
            {isCompleted.toString().toUpperCase()}
          </Tag>
        );
      },
    },
    {
      title: "Action",
      key: "action",
      render: (text, record) => (
        <Space size="middle">
          <EditTwoTone className="edit" onClick={() => onClickEdit(record)}/>
          <DeleteTwoTone className="delete" onClick={() => onClickDelete(record)} />
        </Space>
      ),
    },
  ];

  //define data for table
  const data = todoItems;
  console.log(todoItems);
  return (
    <div className="TodoNew">
      <Search
        placeholder="input search text"
        allowClear
        enterButton="Search"
        size="large"
        onSearch={onSearch}
      />
      <Button type="primary" size="large">
        +Add
      </Button>
      <Table columns={columns} dataSource={data} />
      <EditModal visible={false}/>
      <AddModal visible={false}/>
    </div>
  );
}

export default TodoNew;
