import { React, useEffect, useState } from "react";
import "./TodoNew.css";
import axios from "axios";
import { Table, Tag, Space, Button, Input, Row, Col, Pagination } from "antd";
import { EditTwoTone, DeleteTwoTone } from "@ant-design/icons";
import EditModal from "./EditModal";
import AddModal from "./AddModal";

function Paging() {
  const [contacts, setContacts] = useState([]);
  const [pagination, setPagination] = useState({ current: 1, pageSize: 5 });
  const [loading, setLoading] = useState(false);
  const [editVisible, setEditVisible] = useState(false);
  const [addVisible, setAddVisible] = useState(false);
  const [contact, setContact] = useState([]);

  function fetch(page) {
    setLoading(true);
    axios
      .get(
        "https://gateway.smartcontactcenter.xyz/smart-contact-center-contact-list-management/api/v1/omni-contact-list-normalizations",
        {
          params: {
            filter: {
              limit: page.pageSize,
              offset: page.current - 1,
            },
          },
        }
      )
      .then((res) => {
        let params = res.data.pop();
        setContacts(res.data);
        setPagination({
          currnet: params.offset,
          pageSize: params.limit,
          total: params.total,
        });
        setLoading(false);
      })
      .catch((err) => {
        console.log(err);
      });
  }

  useEffect(() => {
    fetch(pagination);
  }, []);

  const handleTableChange = (page, pageSize) => {
    let currentPage = {current: page, pageSize: pageSize}
    fetch(currentPage);
  }
  //search todo
  const { Search } = Input;

  const onSearch = (value) => {
    // if (value !== "") {
    //   let listSearch = [];
    //   for (let item of todoItems) {
    //     if (item.title.includes(value)) {
    //       listSearch.push(item);
    //     }
    //   }
    //   setTodo(listSearch);
    // } else {
    //   axios
    //     .get("https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data")
    //     .then((res) => {
    //       setTodo(res.data);
    //     })
    //     .catch((err) => {
    //       console.log(err);
    //     });
    // }
  };

  //click edit
  const onClickEdit = (item) => {
    // setItem(item);
    // setEditVisible(true);
  };

  const getEditUpdate = (item) => {
    // const index = findIndex(todoItems, item);
    // setTodo([
    //   ...todoItems.slice(0, index),
    //   {
    //     id: item.id,
    //     title: item.title,
    //     isEditing: item.isEditing,
    //     isCompleted: item.isCompleted,
    //   },
    //   ...todoItems.slice(index + 1),
    // ]);
    // setEditVisible(false);
  };

  //click add
  const onClickAdd = () => {
    setAddVisible(true);
  };

  const getAddUpdate = (item) => {
    // setTodo([
    //   ...todoItems.slice(0, todoItems.length),
    //   {
    //     id: todoItems.length + 1,
    //     title: item.title,
    //     isEditing: item.isEditing,
    //     isCompleted: item.isCompleted,
    //   },
    // ]);
    setAddVisible(false);
  };

  const setVisibleFalse = () => {
    setEditVisible(false);
    setAddVisible(false);
  };

  //click delete
  const findIndex = (todoList, item) => {
    for (let i = 0; i < todoList.length; i++) {
      if (todoList[i].id === item.id) {
        return i;
      }
    }
  };

  const onClickDelete = (item) => {
    // const temp = todoItems;
    // const index = findIndex(temp, item);
    // setTodo([...todoItems.slice(0, index), ...todoItems.slice(index + 1)]);
    // axios
    //   .delete(
    //     "https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data/" +
    //       item.id
    //   )
    //   .then((response) => {
    //     console.log(response);
    //   });
  };

  //define table
  const columns = [
    {
      title: "id",
      dataIndex: "id",
      key: "id",
      width: "15%",
    },
    {
      title: "Họ tên",
      dataIndex: "ho_va_ten",
      key: "ho_va_ten",
    },
    {
      title: "Số điện thoại",
      dataIndex: "sdt",
      key: "sdt",
    },
    {
      title: "Giới tính",
      dataIndex: "gender",
      key: "gender",
      render: (gender) => {
        let color;
        if (gender === "male") {
          color = "volcano";
        }
        if (gender === "female") {
          color = "green";
        }
        return (
          <Tag color={color} key={gender}>
            {gender.toString().toUpperCase()}
          </Tag>
        );
      },
    },
    {
      title: "",
      key: "action",
      render: (text, record) => (
        <Space size="middle">
          <EditTwoTone
            className="edit-button"
            onClick={() => onClickEdit(record)}
          />
          <DeleteTwoTone
            className="delete"
            onClick={() => onClickDelete(record)}
          />
        </Space>
      ),
    },
  ];

  //define data for table
  const data = contacts;
  // const tablePagination = {
  //   pageSize: pagination.pageSize,
  //   page: pagination.current / pagination.pageSize + 1,
  //   total: pagination.total,
  //   pageSizeOptions: ["10", "20", "30", "50", "100"],
  //   showSizeChanger: true,
  //   size: "default",
  // };

  return (
    <div className="TodoNew">
      <Row>
        <Col span={6}>
          <Search
            className="searchTxb"
            placeholder="input search text"
            allowClear
            enterButton="Search"
            size="large"
            onSearch={onSearch}
          />
        </Col>
        <Col span={6}></Col>
        <Col span={6}></Col>
        <Col span={6} className="addButton">
          <Button type="primary" size="large" onClick={onClickAdd}>
            +Add
          </Button>
        </Col>
      </Row>

      <Table
        columns={columns}
        dataSource={data}
        pagination={pagination}
        loading={loading}
        onChange={handleTableChange}
      />

      <EditModal
        key={editVisible}
        visible={editVisible}
        todoItem={contacts}
        getEditUpdate={getEditUpdate}
        setVisibleFalse={setVisibleFalse}
      />
      <AddModal
        key={addVisible}
        visible={addVisible}
        getAddUpdate={getAddUpdate}
        setVisibleFalse={setVisibleFalse}
      />
    </div>
  );
}

export default Paging;
