import React, { Component } from 'react';
import './App.css';
import TodoItem from './components/TodoItem';
import TodoInput from './components/TodoInput';
import TodoFilter from './components/TodoFilter';

class App extends Component {
  constructor() {
    super();
    //create state
    this.state = { todoItems: [
      { 
        title: "Job 1",
        isEditing: false, 
        isCompleted: false
      }, {
        title: "Job 2",
        isEditing: false, 
        isCompleted: true
      }, { 
        title: "Job 3",
        isEditing: false, 
        isCompleted: false
      }
    ]};

    window.localStorage.setItem('listItem', JSON.stringify(this.state));

    //create function non parameter
    this.onPressEnter = this.onPressEnter.bind(this);
    this.chooseAll = this.chooseAll.bind(this);
    this.allFunc = this.allFunc.bind(this);
    this.activeFunc = this.activeFunc.bind(this);
    this.completeFunc = this.completeFunc.bind(this);
    this.clearCompleted = this.clearCompleted.bind(this);
  }

  //to mark complete function
  findIndex(todoItems, item) {
    for(let i = 0; i < todoItems.length; i++) {
      if(todoItems[i].title === item.title) {
        return i;
      }
    }
  }

  onItemClicked(item) {
    return() => {
      const isCompleted = item.isCompleted;
      const { todoItems } = JSON.parse(window.localStorage.getItem('listItem'));
      const index = this.findIndex(todoItems, item);
      const newState = { todoItems: [
        ...todoItems.slice(0, index),
        {
          ...item,
          isCompleted: !isCompleted
        }, 
        ...todoItems.slice(index + 1)
      ]};
      window.localStorage.clear();
      window.localStorage.setItem('listItem', JSON.stringify(newState));
      this.setState(newState);
    }
  }

  //double click to edit text
  onDoubleClick(item) {
    return() => {
      const { todoItems } = this.state;
      const index = todoItems.indexOf(item);
      this.setState({ todoItems: [
        ...todoItems.slice(0, index),
        { 
          ...item, 
          isEditing: true, 
          isCompleted: item.isCompleted
        },
        ...todoItems.slice(index + 1)
      ]});
    }
  }

  //save text afer edit (click outside textbox)
  componentDidUpdate() {
    console.log(this.state);
  }

  onChange(event, item) {
    const text = event.target.value;
    const isCompleted = item.isCompleted;
    const { todoItems } = this.state;
    const index = this.findIndex(todoItems, item);
    const newState = { todoItems: [
      ...todoItems.slice(0, index),
      {
        title: text,
        isEditing: true,
        isCompleted: isCompleted
      }, 
      ...todoItems.slice(index + 1)
    ]};
    this.setState(newState);
  }

  onBlur(event, item) {
    return() => {
      const text = event.target.value;
      console.log(text);
      const isCompleted = item.isCompleted;
      const { todoItems } = JSON.parse(window.localStorage.getItem('listItem'));
      const index = this.findIndex(todoItems, item);
      const newState = { todoItems: [
        ...todoItems.slice(0, index),
        {
          title: text,
          isEditing: false,
          isCompleted: isCompleted
        }, 
        ...todoItems.slice(index + 1)
      ]};
      window.localStorage.clear();
      window.localStorage.setItem('listItem', JSON.stringify(newState));
      this.setState(newState);
    }
  }

  //delete todo 
  onDelete(item) {
    return() => {
      const { todoItems } = JSON.parse(window.localStorage.getItem('listItem'));
      const index = this.findIndex(todoItems, item);
      const newState = { todoItems: [
        ...todoItems.slice(0, index),
        ...todoItems.slice(index + 1)
      ]};
      window.localStorage.clear();
      window.localStorage.setItem('listItem', JSON.stringify(newState));
      this.setState(newState);
    }
  }

  //add todo
  onPressEnter(event) {
    if(event.key === 'Enter') {
      const { todoItems } = JSON.parse(window.localStorage.getItem('listItem'));
      todoItems.push({
          title: event.target.value, 
          isEditing: false,
          isCompleted: false
      });
      this.setState({todoItems: todoItems}, () => {
        window.localStorage.clear();
        window.localStorage.setItem('listItem', JSON.stringify(this.state));
      });
      event.target.value = "";
    }        
  } 

  //click image to complete all
  chooseAll() {
    const { todoItems } = this.state;
    let count = 0
    for(let item of todoItems) {
      if(item.isCompleted === true) {
        count++;
      }
    }
    if(count === todoItems.length) {
      for(let item of todoItems) {
        item.isCompleted = false;
      }
    } else {
      for(let item of todoItems) {
        item.isCompleted = true;
      }
    }

    const newState = {todoItems: todoItems};

    this.setState(newState, () => {
      window.localStorage.clear();
      window.localStorage.setItem('listItem', JSON.stringify(this.state));
    });
  }

  //show all todo
  allFunc() {
    this.setState(JSON.parse(window.localStorage.getItem('listItem')));
  }

  activeFunc() {
    const { todoItems } = JSON.parse(window.localStorage.getItem('listItem'));
    let n = todoItems.length;
    for(let i = 0; i < n; i++) {
      if(todoItems[i].isCompleted === true) {
        todoItems.splice(i, 1);
        i--;
        n--;
      }
    }

    const newState = {todoItems: todoItems};
    this.setState(newState);
  }

  completeFunc() {
    const { todoItems } = JSON.parse(window.localStorage.getItem('listItem'));
    let n = todoItems.length;
    for(let i = 0; i < n; i++) {
      if(todoItems[i].isCompleted === false) {
        todoItems.splice(i, 1);
        i--;
        n--;
      }
    }

    const newState = {todoItems: todoItems};
    this.setState(newState);
  }

  clearCompleted() {
    const { todoItems } = JSON.parse(window.localStorage.getItem('listItem'));
    let n = todoItems.length;
    for(let i = 0; i < n; i++) {
      if(todoItems[i].isCompleted === true) {
        todoItems.splice(i, 1);
        i--;
        n--;
      }
    }

    const newState = {todoItems: todoItems};
    this.setState(newState, () => {
      window.localStorage.clear();
      window.localStorage.setItem('listItem', JSON.stringify(this.state));
    });
  }

  countActive() {
    const { todoItems } = JSON.parse(window.localStorage.getItem('listItem'));
    let n = todoItems.length;
    for(let i = 0; i < n; i++) {
      if(todoItems[i].isCompleted === true) {
        todoItems.splice(i, 1);
        i--;
        n--;
      }
    }
    return n;
  }

  render() {
    const { todoItems } = this.state;
    const numItem = this.countActive();
    return (
      <div className="App"> 
        <p className="title">todos</p>
        <TodoInput onKeyUp={this.onPressEnter} chooseAll={this.chooseAll}/>
        {
          todoItems.map((item, index) => 
            <TodoItem 
            key={index} 
            item={item} 
            onClick={this.onItemClicked(item)}
            onDoubleClick={this.onDoubleClick(item)}
            onChange={(event) => this.onChange(event, item)}
            onBlur={(event) => this.onBlur(event, item)}
            onDelete={this.onDelete(item)}/>)
        }
        <TodoFilter 
        numItem={numItem}
        todoItems={todoItems}
        allFunc={this.allFunc}
        activeFunc={this.activeFunc}
        completeFunc={this.completeFunc}
        clearCompleted={this.clearCompleted}
        />
      </div>
    );
  }

}

export default App;
