import React, { useState } from "react";
import { Modal, Form, Input } from "antd";
import axios from "axios";
const layout = {
  labelCol: { span: 8 },
  wrapperCol: { span: 16 },
};

function EditModal(props) {
  const [isModalVisible, setIsModalVisible] = useState(props.visible);

  const handleOk = () => {
    axios
      .put(
        "https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data/" +
          todoItems[i].id,
        {
          isCompleted: todoItems[i].isCompleted,
        }
      )
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };
  console.log(isModalVisible);
  return (
    <>
      <Modal
        title="Basic Modal"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form {...layout} name="nest-messages">
          <Form.Item name="id" label="id">
            <Input defaultValue="this is id" />
          </Form.Item>
          <Form.Item name="title" label="title">
            <Input defaultValue="this is title" />
          </Form.Item>
          <Form.Item name="isEditing" label="isEditing">
            <Input defaultValue="this is isEditing" />
          </Form.Item>
          <Form.Item name="isCompleted" label="isCompleted">
            <Input defaultValue="this is isCompleted" />
          </Form.Item>
        </Form>
      </Modal>
    </>
  );
}

export default EditModal;
