import { React, useEffect, useState } from "react";
import "./TodoNew.css";
import axios from "axios";
import { Table, Tag, Space, Button, Input } from "antd";
import { EditTwoTone, DeleteTwoTone } from "@ant-design/icons";

import EditModal from "./EditModal";
import AddModal from "./AddModal";
const { Search } = Input;
const onSearch = (value) => console.log(value);

function TodoNew() {
  const [todoItems, setTodo] = useState([]);
  const [editVisible, setEditVisible] = useState(false);
  const [addVisible, setAddVisible] = useState(false);
  const [todoItem, setItem] = useState({});

  useEffect(() => {
    axios
      .get("https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data")
      .then((res) => {
        setTodo(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  //click edit
  const onClickEdit = (item) => {
    setItem(item);
    setEditVisible(true);
  };

  const onClickAdd = () => {
    setAddVisible(true);
  };

  //click delete
  const findIndex = (todoList, item) => {
    for (let i = 0; i < todoList.length; i++) {
      if (todoList[i].id === item.id) {
        return i;
      }
    }
  };

  const onClickDelete = (item) => {
    const temp = todoItems;

    const index = findIndex(temp, item);
    setTodo([...todoItems.slice(0, index), ...todoItems.slice(index + 1)]);

    axios
      .delete(
        "https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data/" +
          item.id
      )
      .then((response) => {
        console.log(response);
      });
  };

  //define table
  const columns = [
    {
      title: "id",
      dataIndex: "id",
      key: "id",
    },
    {
      title: "Title",
      dataIndex: "title",
      key: "title",
    },
    {
      title: "Is editing?",
      dataIndex: "isEditing",
      key: "isEditing",
      render: (isEditing) => {
        let color;
        if (isEditing === "false" ) {
          color = "volcano";
        }
        if (isEditing === "true") {
          color = "green";
        }
        return (
          <Tag color={color} key={isEditing}>
            {isEditing.toString().toUpperCase()}
          </Tag>
        );
      },
    },
    {
      title: "Is complete?",
      dataIndex: "isCompleted",
      key: "isCompleted",
      render: (isCompleted) => {
        let color;
        if (isCompleted === "false") {
          color = "volcano";
        }
        if (isCompleted === "true") {
          color = "green";
        }
        return (
          <Tag color={color} key={isCompleted}>
            {isCompleted.toString().toUpperCase()}
          </Tag>
        );
      },
    },
    {
      title: "Action",
      key: "action",
      render: (text, record) => (
        <Space size="middle">
          <EditTwoTone className="edit-button" onClick={() => onClickEdit(record)} />
          <DeleteTwoTone
            className="delete"
            onClick={() => onClickDelete(record)}
          />
        </Space>
      ),
    },
  ];

  //define data for table
  const data = todoItems;

  return (
    <div className="TodoNew">
      <Search
        placeholder="input search text"
        allowClear
        enterButton="Search"
        size="large"
        onSearch={onSearch}
      />
      <Button type="primary" size="large" onClick={onClickAdd}>
        +Add
      </Button>
      <Table columns={columns} dataSource={data} />
      <EditModal key={editVisible} visible={editVisible} todoItem={todoItem} />
      <AddModal key={addVisible} visible={addVisible} />
    </div>
  );
}

export default TodoNew;
