import React, { Component } from 'react';
import './App.css';
import TodoItem from './components/TodoItem';
class App extends Component {
  constructor() {
    super();
    this.todoItems = [
        { title : 'Job 1', isCompleted : true },
        { title : 'Job 2', isCompleted : true },
        { title : 'Job 3', isCompleted : false }
    ];
    this.state = this.todoItems;
    this.onItemClicked = this.onItemClicked.bind(this);
  }

  onItemClicked(item) {
    var listItem = [];
    for(var i of this.state) {
      listItem.push(i);
    }
    for(var j of listItem) {
      if(this.state[i].title === item.tile) {
        this.setState
      }
    }
  }

  render() {
    return (
      <div className="App"> 
        {
          this.state.map((item, index) => 
            <TodoItem key={index} item={item} onClick={() => this.onItemClicked(item)}/>)
        }
      </div>
    );
  }

}

export default App;
