import React, { Component } from 'react';
import './App.css';
import TodoItem from './components/TodoItem';
class App extends Component {
  constructor() {
    super();
    this.todoItems = [
        { title: 'Job 1', isCompleted : true},
        { title : 'Job 2', isCompleted : true},
        { title : 'Job 3', isCompleted : false}
    ];
  }

  onItemClicked(item) {
    this.state = item ;
    this.setState({ title: item.title, isCompleted: !item.isCompleted });
  }

  render() {
    return (
      <div className="App"> 
        {
          this.todoItems.map((item, index) => 
            <TodoItem key={index} item={item} onClick={this.onItemClicked(item)}/>)
        }
      </div>
    );
  }

}

export default App;
