import React, { useState } from "react";
import { Modal, Form, Input } from "antd";
import axios from "axios";

const layout = {
  labelCol: { span: 8 },
  wrapperCol: { span: 16 },
};

function EditModal(props) {
  const [isModalVisible, setIsModalVisible] = useState(props.visible);

  const handleOk = () => {
    const title = document.getElementById("nest-messages_title").value;
    const isEditing = document.getElementById("nest-messages_isEditing").value;
    const isCompleted = document.getElementById(
      "nest-messages_isCompleted"
    ).value;

    if (
      title !== "" &&
      (isEditing === "true" || isEditing === "false") &&
      (isCompleted === "true" || isCompleted === "false")
    ) {
      const item = props.todoItem;
      axios
        .put(
          "https://60b0f8b91f26610017fff943.mockapi.io/api/v1/todo_data/" +
            item.id,
          {
            ...item,
            title: title,
            isEditing: isEditing,
            isCompleted: isCompleted,
          }
        )
        .then((res) => {
          let temp = props.getEditUpdate;
          temp(res.data);
        })
        .catch((err) => {
          console.log(err);
        });

      setIsModalVisible(false);
    }
  };

  const handleCancel = () => {
    let temp = props.setVisibleFalse;
    temp();
    setIsModalVisible(false);
  };

  return (
    <>
      <Modal
        title="Basic Modal"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form {...layout} name="nest-messages">
          <Form.Item
            label="Họ tên"
            name="ho_va_ten"
            rules={[{ required: true, message: "Vui lòng nhập họ tên" }]}
          >
            <Input defaultValue={props.contactEdit.ho_va_ten} />
          </Form.Item>
          <Form.Item
            label="Số điện thoại"
            name="sdt"
            rules={[
              {
                required: true,
                pattern: new RegExp("([0-9]\\s*)+"),
                message: "Wrong format!",
              },
            ]}
          >
            <Input defaultValue={props.contactEdit.sdt} />
          </Form.Item>
          <Form.Item
            label="Giới tính"
            name="gender"
            rules={[
              {
                required: true,
              },
            ]}
          >
            <Input defaultValue={props.contactEdit.gender} />
          </Form.Item>
          <Form.Item
            label="Tuổi"
            name="age"
            rules={[
              {
                required: true,
              },
            ]}
          >
            <Input defaultValue={props.contactEdit.ho_va_ten}a />
          </Form.Item>
        </Form>
      </Modal>
    </>
  );
}

export default EditModal;
