import * as types from "../constants/ActionTypes";
import axios from "axios";
import * as jwt from "jsonwebtoken";
import bcrypt from "bcryptjs";

export const setCurrentUser = (user) => {
  return {
    type: types.SET_CURRENT_USER,
    user,
  };
};

export const registerRequest = (user) => {
  return (dispatch) => {
    console.log(user);
    const salt = bcrypt.genSaltSync(10);
    const hassPassword = bcrypt.hashSync(user.password, "my salt");
    const token = jwt.sign(
      {
        username: user.username,
        password: hassPassword,
      },
      "SECRET_TOKEN"
    );
    const data = {
      username: user.username,
      password: hassPassword,
      token: token,
    };

    axios
      .post("https://60b0f8b91f26610017fff943.mockapi.io/api/v1/users", data)
      .then((response) => {
        dispatch(register(response.data));
      })
      .catch((err) => {
        console.log(err);
      });
  };
};

export const register = (user) => {
  return {
    type: types.REGISTER,
    user,
  };
};

export const login = (data) => {
  return (dispatch) => {
    return axios
      .get("https://60b0f8b91f26610017fff943.mockapi.io/api/v1/users")
      .then((response) => {
        for (const user of response.data) {
          if (data.username === user.username) {
            bcrypt.compare(data.password, user.password).then((valid) => {
              if (valid) {
                const token = response.data.token;
                window.localStorage.setItem("jwtToken", token);
                dispatch(setCurrentUser(jwt.decode(token)));
              }
            });
          }
        }
      });
  };
};
