import * as types from "../constants/ActionTypes";
import axios from "axios";

export const fetchContactRequest = () => {
  return (dispatch) => {
    axios
      .get("https://60b0f8b91f26610017fff943.mockapi.io/api/v1/contact_data")
      .then((res) => {
        dispatch(fetchContact(res.data));
      })
      .catch((err) => {
        console.log(err);
      });
  };
};

export const fetchContact = (contacts) => {
  return {
    type: types.FETCH_CONTACT,
    contacts,
  };
};

export const delContactRequest = (id) => {
  return (dispatch) => {
    axios
      .delete(
        "https://60b0f8b91f26610017fff943.mockapi.io/api/v1/contact_data/" + id
      )
      .then((response) => {
        dispatch(delContact(response.data));
      });
  };
};

export const delContact = (contact) => {
  return {
    type: types.DELETE_CONTACT,
    contact,
  };
};

export const addContactRequest = (contact) => {
  return (dispatch) => {
    const ho_va_ten = document.getElementById("nest-messages_ho_va_ten").value;
    const sdt = document.getElementById("nest-messages_sdt").value;
    const gender = document.getElementById("nest-messages_gender").value;
    const age = document.getElementById("nest-messages_age").value;

    if (ho_va_ten !== "" && sdt !== "" && gender !== "" && age !== "") {
      axios
        .post(
          "https://60b0f8b91f26610017fff943.mockapi.io/api/v1/contact_data",
          {
            ho_va_ten: ho_va_ten,
            sdt: sdt,
            gender: gender,
            age: age,
          }
        )
        .then((res) => {
          let temp = props.getAddUpdate;
          temp(res.data);
          dispatch(addContact(contact));
        })
        .catch((err) => {
          console.log(err);
        });

      setIsModalVisible(false);
    }
  };
};

