import React, { useState } from "react";
import { Modal, Form, Input } from "antd";
import axios from "axios";
const layout = {
  labelCol: { span: 8 },
  wrapperCol: { span: 16 },
};

function AddModal(props) {
  const [isModalVisible, setIsModalVisible] = useState(props.visible);

  const handleOk = () => {
    const ho_va_ten = document.getElementById("nest-messages_ho_va_ten").value;
    const sdt = document.getElementById("nest-messages_sdt").value;
    const gender = document.getElementById("nest-messages_gender").value;
    const age = document.getElementById("nest-messages_age").value;
    
    if (ho_va_ten !== "" && sdt !== "" && gender !== "" && age !== "") {
      axios
        .post(
          "https://60b0f8b91f26610017fff943.mockapi.io/api/v1/contact_data",
          {
            ho_va_ten: ho_va_ten,
            sdt: sdt,
            gender: gender,
            age: age,
          }
        )
        .then((res) => {
          //let temp = props.getAddUpdate;
          //temp(res.data);
          dispatch(addContact(contact));
        })
        .catch((err) => {
          console.log(err);
        });

      //setIsModalVisible(false);
    }
  };

  const handleCancel = () => {
    let temp = props.setVisibleFalse;
    temp();
    setIsModalVisible(false);
  };

  return (
    <>
      <Modal
        title="Basic Modal"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form {...layout} name="nest-messages">
          <Form.Item
            name="Họ tên"
            label="ho_va_ten"
            rules={[{ required: true, message: "Vui lòng nhập họ tên" }]}
          >
            <Input defaultValue="" />
          </Form.Item>
          <Form.Item
            name="Số điện thoại"
            label="sdt"
            rules={[
              {
                required: true,
                pattern: new RegExp("([0-9]\\s*)+"),
                message: "Wrong format!",
              },
            ]}
          >
            <Input defaultValue="this is isEditing" />
          </Form.Item>
          <Form.Item
            name="Giới tính"
            label="gender"
            rules={[
              {
                required: true,
              },
            ]}
          >
            <Input defaultValue="this is isCompleted" />
          </Form.Item>
          <Form.Item
            name="Tuổi"
            label="age"
            rules={[
              {
                required: true,
              },
            ]}
          >
            <Input defaultValue="12" />
          </Form.Item>
        </Form>
      </Modal>
    </>
  );
}

export default AddModal;
