import { LOGIN } from "../constants/ActionTypes";

const initialState = {
  user: { username: "", password: "", auth: false },
};

const loginReducer = (state = initialState, action) => {
  switch (action.type) {
    case LOGIN:
      return {
        username: action.username,
        password: action.password,
        auth: action.auth,
      };
    default:
      return initialState;
  }
};

export default loginReducer;
